void Mouse::calibrate()
{
	pixelmmX = 59; // 59 pixels/mm or 1500 pixels/inch
	pixelmmY = 75; // 75.1 pixels.mm or 1900 pixels/inch}
}

Mouse::Mouse() // originally void setup()
{
	Serial.begin(19200);
	xydatTotal[0] = 0;
	xydatTotal[1] = 0;
	
	pinMode (PIN_SLAVESELECT, OUTPUT);
	
	pinMode(PIN_INTERRUPT,INPUT);
	//attachInterrupt(PIN_INTERRUPT, mouse.InterruptMOT, FALLING);

	// get SPI going
	SPI.setSCK(14); // alternate SPI pins
	SPI.setMOSI(7);
	SPI.setMISO(8);
	SPI.begin();
	SPI.setDataMode(SPI_MODE3);
	SPI.setBitOrder(MSBFIRST);
	SPI.setClockDivider(8);

	performStartup();
	//dispRegisters(); // show device information over serial
	delay(100);
	//initComplete=9;
	initComplete = true;
	//startTime = millis();
	delay(1000);
	if (digitalRead(PIN_INTERRUPT) == LOW){
		reset();
	}
	lastSendTime = millis(); //Set to current time
}

void Mouse::adns_com_begin()
{
	//DIGITAL_WRITE_10_LOW();
	digitalWrite(PIN_SLAVESELECT, LOW);
}

void Mouse::adns_com_end()
{
	//DIGITAL_WRITE_10_HIGH();
	digitalWrite(PIN_SLAVESELECT, HIGH);
}

byte Mouse::adns_read_reg(byte reg_addr)
{
	adns_com_begin();
	
	// send adress of the register, with MSBit = 0 to indicate it's a read
	SPI.transfer(reg_addr & 0x7f );
	delayMicroseconds(100); // tSRAD
	// read data
	byte data = SPI.transfer(0);
	
	delayMicroseconds(1); // tSCLK-NCS for read operation is 120ns
	adns_com_end();
	delayMicroseconds(19); //  tSRW/tSRR (=20us) minus tSCLK-NCS

	return data;
}

void Mouse::adns_write_reg(byte reg_addr, byte data)
{
	adns_com_begin();
	
	//send adress of the register, with MSBit = 1 to indicate it's a write
	SPI.transfer(reg_addr | 0x80 );
	//sent data
	SPI.transfer(data);
	
	delayMicroseconds(20); // tSCLK-NCS for write operation
	adns_com_end();
	delayMicroseconds(100); // tSWW/tSWR (=120us) minus tSCLK-NCS. Could be shortened, but is looks like a safe lower bound 
}

void Mouse::adns_upload_firmware()
{
	// send the firmware to the chip, cf p.18 of the datasheet
	//Serial.println("Uploading firmware...");
	// set the configuration_IV register in 3k firmware mode
	adns_write_reg(REG_Configuration_IV, 0x02); // bit 1 = 1 for 3k mode, other bits are reserved 
	
	// write 0x1d in SROM_enable reg for initializing
	adns_write_reg(REG_SROM_Enable, 0x1d); 
	
	// wait for more than one frame period
	delay(10); // assume that the frame rate is as low as 100fps... even if it should never be that low
	
	// write 0x18 to SROM_enable to start SROM download
	adns_write_reg(REG_SROM_Enable, 0x18); 
	
	// write the SROM file (=firmware data) 
	adns_com_begin();
	SPI.transfer(REG_SROM_Load_Burst | 0x80); // write burst destination adress
	delayMicroseconds(15);
	
	// send all bytes of the firmware
	unsigned char c;
	for(int i = 0; i < firmware_length; i++){ 
		c = (unsigned char)pgm_read_byte(firmware_data + i);
		SPI.transfer(c);
		delayMicroseconds(15);
	}
	adns_com_end();
}


void Mouse::performStartup(void)
{
	adns_com_end(); // ensure that the serial port is reset
	adns_com_begin(); // ensure that the serial port is reset
	adns_com_end(); // ensure that the serial port is reset
	adns_write_reg(REG_Power_Up_Reset, 0x5a); // force reset
	delay(50); // wait for it to reboot
	// read registers 0x02 to 0x06 (and discard the data)
	adns_read_reg(REG_Motion);
	adns_read_reg(REG_Delta_X_L);
	adns_read_reg(REG_Delta_X_H);
	adns_read_reg(REG_Delta_Y_L);
	adns_read_reg(REG_Delta_Y_H);
	// upload the firmware
	adns_upload_firmware();
	delay(10);
	//enable laser(bit 0 = 0b), in normal mode (bits 3,2,1 = 000b)
	// reading the actual value of the register is important because the real
	// default value is different from what is said in the datasheet, and if you
	// change the reserved bytes (like by writing 0x00...) it would not work.
	byte laser_ctrl0 = adns_read_reg(REG_LASER_CTRL0);
	adns_write_reg(REG_LASER_CTRL0, laser_ctrl0 & 0xf0 );
	
	// Turn on snap
	byte last_snap = adns_read_reg(REG_SNAP_ANGLE);
	adns_write_reg(REG_SNAP_ANGLE, last_snap | 0x80);

	
	delay(1);

	Serial.println("Optical Chip Initialized");
}

