uint8_t calculateParity(uint8_t val)
{
	uint8_t parity = 0;
	// While val still has bits set
	for (; val > 0; val >>= 1) {
		// Flip our parity bit if the lowest bit is set.
		parity ^= (val & 0x1);
		// Shift off the lowest bit after every iteration.
	}
	return parity;
}
